
public class Double_MIN_Value {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(Double.MIN_VALUE);
		System.out.println(Double.MAX_VALUE);
		System.out.println(Long.MIN_VALUE);//negative number
		System.out.println(Integer.MIN_VALUE);//-ve number
		System.out.println(Math.min(Double.MIN_VALUE, 0.0d));//+ve number
		System.out.println(Math.min(Integer.MIN_VALUE, 0.0d));
		System.out.println(Math.min(Float.MIN_VALUE, 0.0f));
		System.out.println(Float.MIN_VALUE);

	}

}
