
public class NumberdividedbyZero {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(9.0/0.0);
		//System.out.println(0/0);
		System.out.println(0.0/0);
		System.out.println(0.0%0.0);//arithmetic exception only throws for Integer value not for 
		//double and float
		System.out.println(12.09d/0.0d);
		
		System.out.println(0.0001f/0.0f);

	}

}
